import React from 'react';
import {useState, useEffect} from 'react';
import Api from './Api'
import {Button, Container, NavLink, Card, Row} from "react-bootstrap";

const Simulation = () => {
    const [elevators, setElevators] = useState([])

    const getElevatorsFromDb = () => {
        Api.get("api/v1/elevators"
             ).then(res => {
                 setElevators(res.data)
        }).catch(e => {
            console.log(e)
        })
    }

    useEffect(() => {
        getElevatorsFromDb()
    }, [])

    return (
        <div>
            <Container style={{ width:'12rem', marginLeft: "auto", marginRight:"auto",marginTop:"20px" }}>
                <NavLink  href="/addElevator"
                          style={{ width:'12rem', marginLeft: "auto", marginRight:"auto",marginBottom:"20px" }}>
                    <Button variant="light">Add elevator</Button>
                </NavLink>
            </Container>
            <Container>
                {elevators.map((elevator, index) => (
                            <Card key={index} style={{ width: '18rem' }}>
                                <Card.Body>
                                    <Card.Title>Card Title</Card.Title>
                                    <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
                                    <Card.Text>
                                        Some quick example text to build on the card title and make up the bulk of
                                        the card's content.
                                    </Card.Text>
                                    <Card.Link href="#">Card Link</Card.Link>
                                    <Card.Link href="#">Another Link</Card.Link>
                                </Card.Body>
                            </Card>
                ))}
            </Container>
        </div>
    );
};

export default Simulation;
